$(document).ready(function() {

  "use strict"; 
   // Get cards
    var cards = $('.product-item');
var maxHeight = 0;

// Loop all cards and check height, if bigger than max then save it
for (var i = 0; i < cards.length; i++) {
    if (maxHeight < $(cards[i]).outerHeight()) {
        maxHeight = $(cards[i]).outerHeight();
        // alert(maxHeight);
    }
}
// Set ALL card bodies to this height
for (var i = 0; i < cards.length; i++) {
    $(cards[i]).height(maxHeight);
}
 
    $('input[name="triptype"]').click(function () { 
        $(this).tab('show');
        $(this).removeClass('active');
    });

    $('.datepicker').datepicker({
        format: 'dd/mm/yyyy'
    });
 
    $('.js-main-slider').slick({
        infinite: true,
        arrows: false,
        dots: true,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplaySpeed: 4000,
        autoplay: true,
        prevArrow: "<i class='ti-angle-left slick-arrow prev-arrow'></i>",
        nextArrow: "<i class='ti-angle-right slick-arrow next-arrow'></i>",
    });

    $('.js-products-slider').slick({
        dots: false,
        arrows: false, 
        infinite: false,
        swipeToSlide: true,
        autoplay: false, 
        slidesToShow: 5, 
        variableWidth: true,
        prevArrow: "<i class='ti-angle-left slick-arrow prev-arrow'></i>",
        nextArrow: "<i class='ti-angle-right slick-arrow next-arrow'></i>",
        
    });
    

    $('.js-item2-slider').slick({
        dots: false,
        arrows: true, 
        autoplay: false, 
        slidesToShow: 2,
        prevArrow: "<i class='ti-angle-left slick-arrow prev-arrow'></i>",
        nextArrow: "<i class='ti-angle-right slick-arrow next-arrow'></i>",
        responsive: [  {
            breakpoint: 1024,
            settings: { 
                slidesToShow: 1,
                slidesToScroll: 1
            }
        }]
    });
    // Remove active class from all thumbnail slides
    $('.product-slider-nav-thumbnails .slick-slide').removeClass('slick-active');

    // Set active class to first thumbnail slides
    $('.product-slider-nav-thumbnails .slick-slide').eq(0).addClass('slick-active');

    // On before slide change match active thumbnail to current slide
    $('.product-slider').on('beforeChange', function (event, slick, currentSlide, nextSlide) {
        var mySlideNumber = nextSlide;
        $('.product-slider-nav-thumbnails .slick-slide').removeClass('slick-active');
        $('.product-slider-nav-thumbnails .slick-slide').eq(mySlideNumber).addClass('slick-active');
    });

    function toggleBackToTopBtn() {
        var amountScrolled = 1000;
        if ($(window).scrollTop() > amountScrolled) {
            $("a.scroll-top").fadeIn("slow");
        } else {
            $("a.scroll-top").fadeOut("slow");
        }
    }

    $(".scroll-top").on("click", function() {
        $("html,body").animate({
            scrollTop: 0
        }, 700);
        return false;
    })    



    // Toggle mobile navigation 
    $('[data-toggle="offcanvas"]').on('click', function () {
        $(this).toggleClass('open');
        $('.offcanvas-collapse').toggleClass('open');
    });
     
    // aos animation
    //  AOS.init(); 
    
    $(window).on("scroll", function () {
    var lastScrollTop = '';

    function stickyMenu() {
        var st = $(window).scrollTop();  
        var mainMenuBottom = $('#top-header');

        if ($(window).scrollTop() > 110) {
            if (st > lastScrollTop) {
                //to hide sticky menu on scroll down addClass replace to removeclass
                mainMenuBottom.addClass("sticky-on");

            } else {
                // active sticky menu on scroll up
                mainMenuBottom.removeClass("sticky-on");
            }

        } else {
            mainMenuBottom.removeClass("sticky-on");
        }

        lastScrollTop = st;
    }
    stickyMenu();

    });
});
 

  